CREATE TABLE "public"."authorization_codes" ("id" int8 NOT NULL, "user_id" int8 NOT NULL, "client_id" uuid NOT NULL, "redirect_uri" text NOT NULL, "code" text NOT NULL, "expires_at" Timestamp NOT NULL, "used_at" Timestamp, "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("client_id") REFERENCES "public"."clients"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"));
